import { useNavigate , useParams } from "react-router-dom";
import { checkAuth } from "../functions/Authorization";
import {useEffect,useState} from "react"
import {initSocketConnection,playVideoToOthers,videoStateChanged} from "../functions/roomConnection"
import axios from "axios";
import YouResult from "../components/YouResult";
import ReactPlayer from "react-player";

const ipaddress = "192.168.100.148"


function ChatRoom(){

    const[videos,setVideos] = useState([]);
    const[url,setUrl] = useState("");
    const[state,setState] = useState(false);

    const navigate = useNavigate();
    const roomID = useParams().params;

    useEffect(()=>{
        checkAuth(navigate);
        initSocketConnection(roomID,setUrl,setState);
    // eslint-disable-next-line react-hooks/exhaustive-deps
    },[]);

    function searchVideo(e){
        e.preventDefault();
        const query =  document.getElementById("you-video").value;
        const url  = `https://www.googleapis.com/youtube/v3/search?key=AIzaSyDg5qXORRGImzmPysBcIWI5f09avJ8U-3E&part=snippet&q=${query}&maxResults=${10}&type=video`
        axios.get(url).then((res)=>{
            setVideos(res.data.items);
        })
    }

    function playVideo(id){
        setVideos([]);
        setUrl(`https://www.youtube.com/watch?v=${id}`)
        playVideoToOthers(id)
    }

    function videoState(){
        setState((preValue)=>(!preValue));
        console.log(state);
        videoStateChanged(state);
    }

    return(
        <div>
            <div id = "r-video-container">
            </div>
            <form>
            <input type="text" placeholder="Search video" id="you-video" name="youtube-video" />
            <button onClick={searchVideo}>Search</button>
            </form>
            <div id="searchResults">
                {videos.map((data)=>{
                    const url = `https://img.youtube.com/vi/${data.id.videoId}/0.jpg`
                    return (<YouResult key={data.id.videoId} img={url} cb={playVideo} videoId={data.id.videoId} />)
                })}
            </div>
            <div>
                <ReactPlayer url={url} onPlay={videoState} playing={state}/>
            </div>
        </div>
    )
}

export default ChatRoom;
