import "../styles/toast.css"
import ReactDOM from "react-dom"

function Toast(props){
    return ReactDOM.createPortal(
        <div className="toast-container" >
            <h3>Warning</h3>
            <p>{props.msg}</p>
        </div>,document.getElementById("portal")
    )
}

export default Toast;
