import {useState} from "react";

function SignUp(){

    const [src,setSrc] = useState("");

    function signUp(e){
        e.preventDefault();
        const fileInput = document.getElementById("s-Img");
        const fileDisplay = document.getElementById("s-dis-img");
        let imgData;
        fileInput.addEventListener("change",(e)=>{
            var file = fileInput.files[0];
            if(file){
                var reader = new FileReader();
                reader.onload = function(e) {
                   fileDisplay.src = reader.result;
                   imgData = reader.result;
                }
    
                reader.readAsDataURL(file);	
            }
           
        })

        const User = {
            Name:document.getElementById("s-Uname").value,
            email:document.getElementById("s-Email").value,
            pass:document.getElementById("s-Pass").value,
            img:imgData,  
        }

        console.log(User);
    }
    return(
        <div>
            <form action="http://localhost:5000/signUp" method="POST">
                <input placeholder="Name" id="s-Uname" name="uname" type="text" />
                <input placeholder="E-mail" id="s-Email" name="email" type="email" />
                <input placeholder="Password" id="s-Pass" name="pass" type="password" />
                <input type="file" name="userImage" id="s-Img" accept="image/*"></input>
                <button onClick={signUp}>Submit</button>
            </form>
            <img src="" alt="" id="s-dis-img" />
        </div>
    )
}

export default SignUp;
