import {useState} from "react";
import axios from "axios"

function SignUp(){

    const [src,setSrc] = useState("https://english.cdn.zeenews.com/sites/default/files/2021/12/20/997487-maharashtra-beed-monkeys-dogs-puppies.jpg");
    
    function showImage(){
        let file = document.getElementById("s-Img").files[0];
        if(file){
            var reader = new FileReader();
            reader.onload = function(e) {
                setSrc(reader.result);
            }

            reader.readAsDataURL(file);
        }
           
    }

    function signUp(e){
        e.preventDefault();
        

        const User = {
            Name:document.getElementById("s-Uname").value,
            email:document.getElementById("s-Email").value,
            pass:document.getElementById("s-Pass").value,
            img:document.getElementById("s-Img").files[0],  
        }

        axios.post("http://localhost:5000/signUp",User).then(()=>{
            console.log("send")
        });
        
    }

    function displayImage(){
        var image = document.getElementById("s-dis-img2");

        axios.get("http://localhost:5000/img",{crossdomain:true}).then((res)=>{
            console.log(res.data.img);
        })
    }

    return(
        <div>
            <form action="http://localhost:5000/signUp" method="POST">
                <input placeholder="Name" id="s-Uname" name="uname" type="text" />
                <input placeholder="E-mail" id="s-Email" name="email" type="email" />
                <input placeholder="Password" id="s-Pass" name="pass" type="password" />
                <input onChange={showImage} type="file" name="userImage" id="s-Img" accept="image/*"></input>
                <button onClick={signUp}>Submit</button>
            </form>
            <img width="500px" height="500px" src={src} alt="" id="s-dis-img" />
            <button onClick={displayImage}>Click</button>
            <img width="500px" height="500px" src="" alt="" id="s-dis-img2" />
        </div>
    )
}

export default SignUp;
