import axios from "axios"
import {useEffect,useState} from "react"
import { useNavigate } from "react-router-dom";
import { checkAuth,logout } from "../functions/Authorization";
import Room from "../components/Room"
import CreateRoom from "../components/CreateRoom"
import liveIcon from "../resources/live.png"
import scheduleIcon from "../resources/schedule.png"
import searchIcon from "../resources/search.png"
import profileIcon from "../resources/profile.png"
import "../styles/main.css"
import Profile from "../components/Profile";

function Home(){


    const [rooms,setRooms] = useState([]);
    const [roomModal,setRoomModal] = useState(false);
    const navigate = useNavigate();
    

    useEffect(()=>{
        checkAuth(navigate)
        getRooms(setRooms);  
    // eslint-disable-next-line react-hooks/exhaustive-deps
    },[]);

    function toggleRoomModal(){
        setRoomModal((pre)=>!pre);
    }

    

    return(
        <div>
            <header>
                <div id="header">
                    <span>
                        <h1>Buzzz.live</h1>
                        <span><hr /> <span>.</span></span>
                    </span>
                </div>
            </header>
            
             <button onClick={logout.bind(this,navigate)}>Logout</button>


             <CreateRoom getRooms={getRooms} setRooms={setRooms} onClose={toggleRoomModal} open={roomModal}/>
             
             <section>
                <ul className="nav" >
                    <li className="active" onClick={toggleNav}><a href="/"><img src={liveIcon} alt="live-icon" name="home"/></a></li>
                    <li onClick={toggleNav}><a href="/"><img src={searchIcon} alt="live-icon" name="search"/></a></li>
                    <li onClick={toggleNav}><a href="/"><img src={profileIcon} alt="live-icon" name="profile"/></a></li>
                    <li onClick={toggleNav}><a href="/"><img src={scheduleIcon} alt="live-icon" name="schedule" /></a></li>
                </ul>

                <div>
                    <button onClick={toggleRoomModal} className="create-btn">Create +</button>
                    <div id="rooms-container">
                        {rooms.map((room,index)=>{
                            return(<Room key={room._id} title={room.title} description={room.description} npeople={room.npeople} language={room.lang} img={room.img} roomID={room.roomID} host={room.host} />)
                        })}
                    </div>
                </div>
                 
             </section>
        </div>
    )
}

function toggleNav(e){
    e.preventDefault()
    const listItems = document.querySelectorAll(".nav li");
    console.log(listItems[0].firstChild.lastChild.name);
    listItems.forEach((items)=>{
        if(items.firstChild.lastChild.name===e.target.name){
            items.classList.add("active")
        }else{
            items.classList.remove("active")
        }
    })
}




function getRooms(setRooms){
    axios.get("http://localhost:5000/getRoom").then((res)=>{
        setRooms(res.data)
    })
}

export default Home;
