import { useNavigate , useParams } from "react-router-dom";
import { checkAuth } from "../functions/Authorization";
import {initSocketConnection,userChoice} from "../functions/roomConnection"
import YoutubeSearch from "../components/YoutubeSearch";
import {useState,useRef,useEffect} from "react"
import ChatBox from "../components/ChatBox"
import "../styles/chatRoom.css"
import chatIcon from "../resources/chat.png"
import applicationIcon from "../resources/application.png"
import youtubeIcon from "../resources/youtube.png"
import boardIcon from "../resources/white-board.png"
import YoutubePlayer from "../components/YoutubePlayer";
import videoChatIcon from "../resources/video-chat.png"
import videoIcon from "../resources/video.png"
import novideoIcon from "../resources/no-video.png"
import micIcon from "../resources/microphone.png"
import noMicIcon from "../resources/no-microphone.png"


function ChatRoom(){

    
    const [view,setView] = useState("videochat");
    const navigate = useNavigate();
    const roomID = useParams().params;
    const [videoMedia,setvideoMedia] = useState(videoIcon);
    const [micMedia,setMicMedia] = useState(micIcon);
    
    const[url,setUrl] = useState("");
    const[client,setClient] = useState(false);
    const [clientState,setClientState] = useState(true);
    const clientRef  = useRef();

    
    useEffect(()=>{
        checkAuth(navigate);
        initSocketConnection(roomID,playVideoClient,setClientState,clientRef,createMessage,setView);
    // eslint-disable-next-line react-hooks/exhaustive-deps
    },[]);
    

    function createMessage(msg){
        const container = document.getElementById("chat-message-container");
        const p = document.createElement("p");
        p.innerText = localStorage.getItem("uname") +": "+ msg;
        container.appendChild(p);
    }



   function switchTab(e){
       if(e==="more"){
           document.getElementById("chat-btn").classList.remove("active");
           document.querySelector(".chat-container").classList.remove("activeTab")
           document.getElementById("more-btn").classList.add("active");
           document.querySelector(".more-container").classList.add("activeTab")
           
       }else if(e==="chat"){
            document.getElementById("chat-btn").classList.add("active");
            document.querySelector(".chat-container").classList.add("activeTab")
            document.getElementById("more-btn").classList.remove("active");
            document.querySelector(".more-container").classList.remove("activeTab")
       }
       document.querySelector(".youtube-result-container").classList.remove("activeTab")
   }

   function showYoutube(){
        document.querySelector(".chat-container").classList.remove("activeTab")
        document.querySelector(".more-container").classList.remove("activeTab")
        document.querySelector(".youtube-result-container").classList.add("activeTab")
   }

   function playVideo(vURL){
       setView("youtube")
       console.log(vURL);
       setUrl(vURL);
       setClient(false);
   }

   function playVideoClient(vURL){
        setView("youtube")
        setUrl(vURL);
        setClient(true);
   }

   function hostChoice(choice){
       userChoice(choice)
       setView(choice);
   }

   function toggleUserVideo(){
       setvideoMedia((pre)=>{
           return ((pre===videoIcon)?novideoIcon:videoIcon)
       })
   }

   function toggleUserMic(){
        setMicMedia((pre)=>{
            return ((pre===micIcon)?noMicIcon:micIcon)
        })
   }
   
    return(
        <div className="chatRoom-container">

            <div className="content-container">
                <div className="main-content-container">
                    {(view==="youtube")?<YoutubePlayer url={url} client={client} clientState={clientState} clientRef={clientRef}/>:"" }
                    {(view==="videochat"?<div id = "r-video-container" ></div>:"")}
                    {(view==="whiteboard"?<iframe id="room-whiteboard" src={`https://witeboard.com/${roomID}`} title="justexplore"></iframe>:"")}
                </div>
                <div className="user-profile-container">
                    <div className="media-controls">
                        <button onClick={toggleUserVideo} ><img src={videoMedia} alt="videoICon"/></button>
                        <button onClick={toggleUserMic} ><img src={micMedia} alt="micIcon"/></button>
                    </div>
                </div>    
            </div>

            <div className="tools-container">
                <div className="tab-container">
                    <button id="chat-btn" className="active" onClick={switchTab.bind(this,"chat")} ><img src={chatIcon} alt="chatIcon"/></button>
                    <button id="more-btn" onClick={switchTab.bind(this,"more")} ><img src={applicationIcon} alt="appIcon" /></button>
                </div>
                <div className="chat-container activeTab">
                    <ChatBox createMessage={createMessage}/>
                </div>
                <div className="more-container">
                    <button><img width="50px" onClick={hostChoice.bind(this,"videochat")} src={videoChatIcon} alt="youtubeIcon"/></button>
                    <button><img  width="50px" onClick={showYoutube} src={youtubeIcon} alt="youtubeIcon"/></button>
                    <button><img width="50px" onClick={hostChoice.bind(this,"whiteboard")} src={boardIcon} alt="youtubeIcon"/></button>
                </div>
                <div className="youtube-result-container">
                    <YoutubeSearch cb={playVideo}/>
                </div>
            </div>

            {/*
            
            

             */}
        </div>
    )
}

export default ChatRoom;
