import { useNavigate , useParams } from "react-router-dom";
import { checkAuth } from "../functions/Authorization";
import {useEffect} from "react"
import {initSocketConnection,sendMessageToOthers} from "../functions/roomConnection"
import YoutubePlayer from "../components/YoutubePlayer";
import {useState,useRef} from "react"



function ChatRoom(){

    

    const navigate = useNavigate();
    const roomID = useParams().params;

    
    const[clientUrl,setClientUrl] = useState("");
    const[clientState,setClinetState] = useState(true);
    const clientPlayerRef  = useRef();

    useEffect(()=>{
        checkAuth(navigate);
        initSocketConnection(roomID,setClientUrl,setClinetState,clientPlayerRef);
    // eslint-disable-next-line react-hooks/exhaustive-deps
    },[]);

    function sendMessage(){
        const msg = document.getElementById("chat-message-i").value;
        const container = document.getElementById("chat-message-container");
        const p = document.createElement("p");
        p.innerText = msg;
        container.appendChild(p);
        sendMessageToOthers(msg);
    }

    
    return(
        <div>
            <div id = "r-video-container" ></div>
            <YoutubePlayer clientUrl={clientUrl} setClientUrl={setClientUrl} clientState={clientState} setClinetState={setClinetState} clientPlayerRef={clientPlayerRef}/>
            <div id="chat-message-container"></div>
            <form>
                <input type="text" name="chatMessage" id="chat-message-i"/>
                <button onClick={sendMessage}>Send</button>
            </form>
        </div>
    )
}

export default ChatRoom;
