import axios from "axios";
import profileIcon from "../resources/user.png"
import "../styles/profile.css"
import {useState} from "react";
import ReactDOM from "react-dom"



function Profile(props){
    const [edit,setEdit] = useState(false);

    if(!props.open) return null;

    const username = localStorage.getItem("uname");
    const userImg = localStorage.getItem("img");
    const useremail = localStorage.getItem("email");
    


    function editUser(e){
        if(!edit){
            e.preventDefault();
            const btn = document.getElementById("p-edit-btn");
            document.getElementById("p-pass-btn").classList.add("active")
            document.querySelectorAll(".profile-container input").forEach((element)=>{
                element.removeAttribute('readonly');
                element.classList.add("active");
            })
            btn.innerHTML = "Update";
            setEdit(true);
        }else{
            e.preventDefault();
        }
        
    }
    return ReactDOM.createPortal(
        <div className="profile-container">
            <form>
                <h3>X</h3>
                <img src={(userImg)?userImg:profileIcon} alt="userimage" />
                <input spellCheck="false" autoComplete="off" type="text" name="userName" defaultValue={username} readOnly />
                <input type="email" name="userEmail" defaultValue={useremail} readOnly/>
                <button id="p-pass-btn">Change password</button>
                <button id="p-edit-btn" onClick={editUser}>Edit</button>
            </form>
        </div>,document.getElementById("portal")
    )
}




function getUser(){
    const uID = localStorage.getItem("id");
    axios.post("http://localhost:5000/getUser",{uID:uID}).then((res)=>{
        
    })
}

export default Profile;
