import ReactPlayer from "react-player";
import "../styles/youtubePlayer.css"
import {useRef} from "react"

function YoutubePlayer(props){

    const hostRef = useRef();
    const clientRef = useRef();

    function videoPaused(){

    }

    if(!props.client){
        return (

            <div id="react-player">
                <ReactPlayer ref={hostRef} width="100%" height="100%" url={props.url} controls={true} playing={true} onPause={videoPaused} />
            </div>
            
        )
    }else{
        return (

            <div id="react-player">
                <ReactPlayer width="100%" height="100%" url={props.url} playing={true} />
            </div>
            
        )   
    }
}

export default YoutubePlayer;
